import {
  Circle,
  CircleMarker,
  MapContainer,
  Polyline,
  Polygon,
  Popup,
  Rectangle,
  TileLayer,
  UpdateMapCenter
} from 'react-leaflet';
import React, { Component } from 'react';
import Button from 'react-bootstrap/Button';

const polyline = [
  [51.505, -0.09],
  [51.51, -0.1],
  [51.51, -0.12],
]

const center = [51.505, -0.09];

const multiPolyline = [
  [
    [51.5, -0.1],
    [51.5, -0.12],
    [51.52, -0.12],
  ],
  [
    [51.5, -0.05],
    [51.5, -0.06],
    [51.52, -0.06],
  ],
]


const fillBlueOptions = { fillColor: 'blue' }
const blackOptions = { color: 'black' }
const limeOptions = { color: 'lime' }
const purpleOptions = { color: 'purple' }
const redOptions = { color: 'red' }


class VectorLayersExample extends Component {

  constructor(props){
    super();
    this.state = {
      center: props.center
    };
    this.myRequests = this.myRequests.bind(this);
  }

  myRequests() {
    console.log(this.state.center)
    this.setState({
      center: [43.214, 46.256]
    })
  } 


  render(){
    // console.log(this.props)
    return (
      <div>
        <Button className="mb-3" variant="primary" onClick={ this.myRequests }>Primary</Button>
        <MapContainer center={this.state.center} zoom={13}>
          <TileLayer
            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />
          <Circle center={this.state.center} pathOptions={fillBlueOptions} radius={200} />
          <CircleMarker
            center={[51.51, -0.12]}
            pathOptions={redOptions}
            radius={20}>
            <Popup>Popup in CircleMarker</Popup>
          </CircleMarker>
          <Polyline pathOptions={limeOptions} positions={polyline} />
          <Polyline pathOptions={limeOptions} positions={multiPolyline} />
          <UpdateMapCentre mapCentre={this.state.center} />
        </MapContainer>
      </div>
    );
  }
}

export default VectorLayersExample;
