import './App.css';
import React, { Component } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet'
import Dropdown from 'react-bootstrap/Dropdown';
import Button from 'react-bootstrap/Button';
import InputGroup from 'react-bootstrap/InputGroup';
import FormControl from 'react-bootstrap/FormControl';
import VectorLayersExample from './Maps/MarkerMaps';
import Autosuggest from 'react-autosuggest';


  const languages = [
    {
      name: 'C',
      year: 1972
    },
    {
      name: 'Elm',
      year: 2012
    }
  ];

  const get_result = async(value) => {
      const resp = await fetch('https://api.geocode.earth/v1/autocomplete?text={' + value + '}&api_key=ge-b33c597834fe4b46',{
      method: 'get',
      headers: {
        'Host': 'api.geocode.earth',
        'User-Agent': 'Mozilla/5.0 (Windows NT 10.0; Win64; x64; rv:97.0) Gecko/20100101 Firefox/97.0',
        'Accept': '*/*',
        'Accept-Language': 'en-US,en;q=0.5',
        'Accept-Encoding': 'gzip, deflate , br',
        'Origin': 'https://www.mapzen.com',
        'DNT': '1',
        'Connection': 'keep-alive',
        'Sec-Fetch-Dest': 'empty',
        'Sec-Fetch-Mode': 'no-cors',
        'Sec-Fetch-Site': 'cross-site',
        'TE': 'trailers',
        'Referer': 'https://www.mapzen.com/',
        'Pragma': 'no-cache',
        'Cache-Control': 'no-cache'
      }
    });
      const data1 = await resp.json();
      return data1;
    }

  const getSuggestions = async(value) => {
    const inputValue = value.trim().toLowerCase();
    const inputLength = inputValue.length;

    var res = await get_result(value)
    var data = []
    for(var ind in res.features){
      data.push({
        name: res.features[ind].properties.name,
        lat: res.features[ind].geometry.coordinates[0],
        long: res.features[ind].geometry.coordinates[1],
      })
    }

    console.log(data)

    return data
  };


  const getSuggestionValue = suggestion => suggestion.name;

  const renderSuggestion = suggestion => (
    <div>
      {suggestion.name}
    </div>
  );


class App extends Component {

  constructor(){
    super();
    this.state = {
      value: '',
      suggestions: []
    };
  }


    onChange = (event, { newValue }) => {
      this.setState({
        value: newValue
      });
    };

    // Autosuggest will call this function every time you need to update suggestions.
    // You already implemented this logic above, so just use it.
    onSuggestionsFetchRequested = async({ value }) => {
      this.setState({
        suggestions: await getSuggestions(value)
      });
    };

    // Autosuggest will call this function every time you need to clear suggestions.
    onSuggestionsClearRequested = () => {
      this.setState({
        suggestions: []
      });
    };


  render(){
    const { value, suggestions } = this.state;

    const inputProps = {
      placeholder: 'Type a city name',
      value,
      onChange: this.onChange
    };

  return (
    <div className="flex">

      <div class="outline w-70 pa3 ma2">
        <VectorLayersExample/>
      </div>


      <div class="outline w-30 pa3 ma2">

        <Autosuggest
          suggestions={suggestions}
          onSuggestionsFetchRequested={this.onSuggestionsFetchRequested}
          onSuggestionsClearRequested={this.onSuggestionsClearRequested}
          getSuggestionValue={getSuggestionValue}
          renderSuggestion={renderSuggestion}
          inputProps={inputProps}
        />

        <InputGroup className="mb-3">
          <InputGroup.Text id="inputGroup-sizing-default">Enter City</InputGroup.Text>
          <FormControl
            aria-label="Default"
            aria-describedby="inputGroup-sizing-default"
          />
        </InputGroup>

        <Dropdown>
          <Dropdown.Toggle variant="success" id="dropdown-basic">
            Select ISP
          </Dropdown.Toggle>

          <Dropdown.Menu>
            <Dropdown.Item href="#/action-1">Centurylink</Dropdown.Item>
            <Dropdown.Item href="#/action-2">Xfinity</Dropdown.Item>
            <Dropdown.Item href="#/action-3">Hugesnet</Dropdown.Item>
            <Dropdown.Item href="#/action-3">Viaset</Dropdown.Item>
          </Dropdown.Menu>
        </Dropdown>

        {/*<Button className="mb-3" variant="primary" onClick={ this.myRequests("Chic") }>Primary</Button>{' '}*/}

      </div>

    </div>
  );
}
}

export default App;
