import './App.css';
import React, { Component } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet'
import Dropdown from 'react-bootstrap/Dropdown';
import Button from 'react-bootstrap/Button';
import InputGroup from 'react-bootstrap/InputGroup';
import FormControl from 'react-bootstrap/FormControl';
import VectorLayersExample from './Maps/MarkerMaps';
import Autosuggest from 'react-autosuggest';


const center = [29.926734999999997, -90.085848];

const get_result = async(value) => {
    const resp = await fetch('https://api.geocode.earth/v1/autocomplete?text={' + value + '}&api_key=ge-b33c597834fe4b46',{
    method: 'get',
    headers: {
      'Host': 'api.geocode.earth',
      'User-Agent': 'Mozilla/5.0 (Windows NT 10.0; Win64; x64; rv:97.0) Gecko/20100101 Firefox/97.0',
      'Accept': '*/*',
      'Accept-Language': 'en-US,en;q=0.5',
      'Accept-Encoding': 'gzip, deflate , br',
      'Origin': 'https://www.mapzen.com',
      'DNT': '1',
      'Connection': 'keep-alive',
      'Sec-Fetch-Dest': 'empty',
      'Sec-Fetch-Mode': 'no-cors',
      'Sec-Fetch-Site': 'cross-site',
      'TE': 'trailers',
      'Referer': 'https://www.mapzen.com/',
      'Pragma': 'no-cache',
      'Cache-Control': 'no-cache'
    }
  });
    const data1 = await resp.json();
    return data1;
  }

const getSuggestions = async(value) => {
  const inputValue = value.trim().toLowerCase();
  const inputLength = inputValue.length;

  var res = await get_result(value)
  var data = []
  for(var ind in res.features){
    data.push({
      name: res.features[ind].properties.name,
      location: [res.features[ind].geometry.coordinates[1], res.features[ind].geometry.coordinates[0]]
    })
  }

  return data
};


const getSuggestionValue = suggestion => suggestion.name;

const renderSuggestion = suggestion => (
  <div>
    {suggestion.name}
  </div>
);


function renderColorLabelSingle() {
  return(
    <div>
      <div className="d-flex justify-content-between" style = {{backgroundColor : 'pink'}}>
        <p>0</p>
        <p>13</p>
      </div>
      <hr style={{
        'border': 0,
        'height': '5px',
        'background-image': 'linear-gradient(to right, rgba(255,255,255,255), rgba(0, 8, 122))'
      }} />
      <p class="tc">CV Values</p>
    </div>
  );
}


function renderColorLabelDouble() {
return(
    <div>
      <div className="d-flex justify-content-between" style={{'margin': 0, 'padding': 0}}>
        <p>13</p>
        <p>0</p>
        <p>13</p>
      </div>
      <hr style={{
        'border': 0,
        'height': '5px',
        'background-image': 'linear-gradient(to right, rgba(122, 12, 0), rgba(255,255,255,255), rgba(24, 51, 41))'
      }} />
      <div className="d-flex justify-content-between">
        <p class="f7">(COX Dominates)</p>
        <p>CV Values</p>
        <p class="f7">(ATT Dominates)</p>
      </div>
    </div>
  );
}


class App extends Component {

  constructor(){
    super();
    this.state = {
      value: '',
      suggestions: [],
      center: center,
      isp: 'cox',
      compareTwoISPs: false
    };
  }


  onChange = (event, {newValue}) => {
    this.setState({
      value: newValue
    })
  };

  onSuggestionsFetchRequested = async({ value }) => {
    this.setState({
      suggestions: await getSuggestions(value)
    });
  };

  onSuggestionSelected = (value, {suggestion}) => {
    this.setState({
      center: suggestion.location
    })
  }

  onSuggestionsClearRequested = () => {
    this.setState({
      suggestions: []
    });
  };


  render(){
    const { value, suggestions } = this.state;

    const inputProps = {
      placeholder: 'Type a city name',
      value,
      onChange: this.onChange
    };

    console.log(this.state.isp)

  return (
    <div className="flex">

      <div class="outline w-70 pa3 ma2">
        <VectorLayersExample center={this.state.center} isp={this.state.isp} compareTwoISPs={this.state.compareTwoISPs} />
      </div>


      <div class="outline w-30 pa3 ma2">

        <InputGroup className="mb-3">
          <InputGroup.Text id="inputGroup-sizing-default">Enter City</InputGroup.Text>
          <Autosuggest
            aria-label="Default"
            aria-describedby="inputGroup-sizing-default"
            suggestions={suggestions}
            onSuggestionsFetchRequested={this.onSuggestionsFetchRequested}
            onSuggestionsClearRequested={this.onSuggestionsClearRequested}
            getSuggestionValue={getSuggestionValue}
            renderSuggestion={renderSuggestion}
            onSuggestionSelected={this.onSuggestionSelected}
            inputProps={inputProps}
        />
          {/*<FormControl
            aria-label="Default"
            aria-describedby="inputGroup-sizing-default"
          />*/}
        </InputGroup>

        <Dropdown>
          <Dropdown.Toggle variant="success" id="dropdown-basic">
            Select ISP
          </Dropdown.Toggle>

          <Dropdown.Menu>
            <Dropdown.Item onClick={() => {this.setState({isp: 'cox', compareTwoISPs: false})}}>Cox Comm.</Dropdown.Item>
            <Dropdown.Item onClick={() => {this.setState({isp: 'att', compareTwoISPs: false})}}>AT&T</Dropdown.Item>
            <Dropdown.Item onClick={() => {this.setState({isp: 'centurylink', compareTwoISPs: false})}}>Centurylink</Dropdown.Item>
            <Dropdown.Item onClick={() => {this.setState({isp: 'xfinity', compareTwoISPs: false})}}>Xfinity</Dropdown.Item>
            <Dropdown.Item onClick={() => {this.setState({isp: 'hugesnet', compareTwoISPs: false})}}>Hugesnet</Dropdown.Item>
            <Dropdown.Item onClick={() => {this.setState({isp: 'viaset', compareTwoISPs: false})}}>Viaset</Dropdown.Item>
          </Dropdown.Menu>
        </Dropdown>

        <Button className="mt-3" variant="primary" onClick={() => {this.setState({compareTwoISPs: !this.state.compareTwoISPs})} }>Compare ISPs</Button>{' '}
        {this.state.compareTwoISPs ?
          (renderColorLabelDouble()) :
          (renderColorLabelSingle())
        }
      </div>

    </div>
  );
}
}

export default App;
