const fillBlueOptions = { fillColor: 'blue' }
const blackOptions = { color: 'black' }
const limeOptions = { color: 'lime' }
const purpleOptions = { color: 'purple' }
const redOptions = { color: 'red' }


function UpdateMapCentre(props) {
  const map = useMap();
  map.panTo(props.mapCentre);
  return null;
} 


function readFile(){
  fetch(text)
  .then(r => r.text())
  .then(res => {
    console.log(typeof(res))
    console.log('text decoded:', res);
  })
}


function stringtoArray(string){
  var array = JSON.parse(string);
  return array
}

function parseJSON(string){
  var array = JSON.parse(string);
  return array
}

function csvToArray(str, delimiter = ";") {
  const headers = str.slice(0, str.indexOf("\n")).split(delimiter);
  const rows = str.slice(str.indexOf("\n") + 1).split("\n");
  const arr = rows.map(function (row) {
    const values = row.split(delimiter);
    const el = headers.reduce(function (object, header, index) {
      let colName = header.trim()
      if(colName === "Coordinates" || colName === "cv_cox_dict" || colName === "cv_att_dict"){
        object[colName] = parseJSON(values[index]);
      }
      else if(colName === "cv_att_avg" || colName === "cv_cox_avg"){
        object[colName] = parseFloat(values[index]);
      }
      else{
        object[colName] = values[index];
      }

      return object;
    }, {});
    return el;
  });
  return arr;
}


const readData = async(value) => {
  var res = await fetch(text)
  res = await res.text()
  var parsedData = csvToArray(res)
  // console.log(parsedData)
  return parsedData
};


function makePolygons(data){

  var poly = []
  // console.log(data)
  let sz = data.length
  for(let i = 0; i < sz; i++){
    let opa = data[i]['cv_cox_avg']/10
    poly.push(
      <Polygon 
        key={i}
        pathOptions={{
          color: 'purple',
          fillOpacity: opa
        }}
        positions={data[i]['Coordinates']}
        // eventHandlers={{
        //   mouseover: () => {console.log(i)}
        // }}
      >
      <Tooltip>{"My Carriage value is: " + data[i]['cv_cox_avg']}</Tooltip>
      </Polygon>
    )
  }

  return poly
}



class VectorLayersExample extends Component {


  constructor(props){
    super(props);
    this.state = {
      res: []
    }
  }

  async componentDidMount() {
    var rr = await readData()
    this.setState({ res: rr });
  }

  render(){

    return (
      <div>
        {/*<Button className="mb-3" variant="primary" onClick={ this.myRequests }>Primary</Button>*/}
        <MapContainer center={this.props.center} zoom={13}>
          <TileLayer
            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />
          <Circle center={this.props.center} pathOptions={fillBlueOptions} radius={200} />
          {/*<MarkPositions/>*/}
          <CircleMarker
            center={center}
            pathOptions={redOptions}
            radius={20}
            data-tip="hello world"
            >
            <Popup>Popup in CircleMarker</Popup>
          </CircleMarker>
          <Polyline pathOptions={limeOptions} positions={polyline} />
          <Polyline pathOptions={limeOptions} positions={multiPolyline} />
          {makePolygons(this.state.res)}
          <UpdateMapCentre mapCentre={this.props.center} />
        </MapContainer>
      </div>
    );
  }
}

export default VectorLayersExample;
