import {
  Circle,
  CircleMarker,
  MapContainer,
  Polyline,
  Polygon,
  Popup,
  Rectangle,
  TileLayer,
  UpdateMapCenter,
  useMap
} from 'react-leaflet';
import React, { useState } from 'react';
import Button from 'react-bootstrap/Button';

const polyline = [
  [51.505, -0.09],
  [51.51, -0.1],
  [51.51, -0.12],
]

const center = [-91.323133, 39.921028];

const multiPolyline = [
  [
    [51.5, -0.1],
    [51.5, -0.12],
    [51.52, -0.12],
  ],
  [
    [51.5, -0.05],
    [51.5, -0.06],
    [51.52, -0.06],
  ],
]

const polygon = [
  [51.515, -0.09],
  [51.52, -0.1],
  [51.52, -0.12],
]


function readFile(){
  fetch('./../../../Output.txt')
  .then(res => res.text())
  .then(res => {
    return res
  })
}


const multiPolygon = readFile()


const rectangle = [
  [51.49, -0.08],
  [51.5, -0.06],
]

const fillBlueOptions = { fillColor: 'blue' }
const blackOptions = { color: 'black' }
const limeOptions = { color: 'lime' }
const purpleOptions = { color: 'purple' }
const redOptions = { color: 'red' }


function UpdateMapCentre(props) {
  const map = useMap();
  map.panTo(props.mapCentre);
  return null;
} 

// function MarkPositions() {

//   return 
//   (<div>
//     {for (var i = 0; i <= 10; i++) {
//       <CircleMarker
//         center={[51.51+i/10, -0.12+i/10]}
//         pathOptions={redOptions}
//         radius={20}>
//         <Popup>Popup in CircleMarker</Popup>
//       </CircleMarker>
//     }}
//     </div>
//   )
// } 


function returnPolygon(){
  const items = []

  for (var i = 0; i <= 5; i++) {
    items.push(<Polygon pathOptions={purpleOptions} positions={polygon} />)
  }

  return items
}


function VectorLayersExample(props) {

  // const items = []

  // for (var i = 0; i <= 10; i++) {
  //   items.push(<Polygon pathOptions={purpleOptions} positions={polygon} />)
  // }

  readFile()

  function handleMouseOver(){
    console.log("Hello")
  }

  return (
    <div>
      {/*<Button className="mb-3" variant="primary" onClick={ this.myRequests }>Primary</Button>*/}
      <MapContainer center={props.center} zoom={13}>
        <TileLayer
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />
        <Circle center={props.center} pathOptions={fillBlueOptions} radius={200} />
        {/*<MarkPositions/>*/}
        <CircleMarker
          center={center}
          pathOptions={redOptions}
          radius={20}>
          <Popup>Popup in CircleMarker</Popup>
        </CircleMarker>
        <Polyline pathOptions={limeOptions} positions={polyline} />
        <Polyline pathOptions={limeOptions} positions={multiPolyline} />
        {/*<Polygon pathOptions={purpleOptions} positions={polygon} />*/}
        <Polygon pathOptions={purpleOptions} positions={multiPolygon} onMouseOver={handleMouseOver} onClick = {handleMouseOver}/>
        {/*{returnPolygon()}*/}
        <Rectangle bounds={rectangle} pathOptions={blackOptions} />
        <UpdateMapCentre mapCentre={props.center} />
      </MapContainer>
    </div>
  );
}

export default VectorLayersExample;
